package com.uc.moudle.user.api.exception;/** * @author 9527 * @ClassName SignatureException * @Date 2020/2/13 * @Description TODO * @Version 1.0 */public class SignatureException extends Exception{    private Integer code;    private String message;    private Throwable throwable;    public Integer getCode() {        return code;    }    public Throwable getThrowable() {        return throwable;    }    public void setCode(Integer code) {        this.code = code;    }    public void setMessage(String message) {        this.message = message;    }    @Override    public String getMessage() {        return message;    }    public void setThrowable(Throwable throwable) {        this.throwable = throwable;    }    public SignatureException(){        super();    }    public SignatureException(Integer code, String message) {        this.code = code;        this.message = message;    }    public SignatureException(Integer code, String message, Throwable throwable) {        this.code = code;        this.message = message;        this.throwable = throwable;    }}