package com.uc.moudle.user.provider.config.ribbon;import com.netflix.client.config.IClientConfig;import com.netflix.loadbalancer.AbstractLoadBalancerRule;import com.netflix.loadbalancer.ILoadBalancer;import com.netflix.loadbalancer.Server;import java.util.List;/** * @author 9527 * @ClassName CustomIRule * @Date 2020/1/19 * @Description TODO * @Version 1.0 */public class CustomIRule extends AbstractLoadBalancerRule {    @Override    public Server choose(Object key) {        ILoadBalancer iLoadBalancer = getLoadBalancer();        List<Server> servers = iLoadBalancer.getReachableServers();        if(servers.size() == 0){            return null;        }        Server targetServer = servers.get(servers.size()-1);        return targetServer;    }    @Override    public void initWithNiwsConfig(IClientConfig clientConfig) {    }}