package com.uc.moudle.user.provider.config.spring;import com.uc.moudle.user.provider.config.interceptors.CustomRequestInterceptor;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Configuration;import org.springframework.web.servlet.config.annotation.InterceptorRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;/** * @author 9527 * @ClassName CustomSpringWebConfiguration * @Date 2020/1/21 * @Description TODO * @Version 1.0 */@Configurationpublic class CustomSpringWebConfiguration implements WebMvcConfigurer {    @Autowired    private CustomRequestInterceptor customRequestInterceptor;    @Override    public void addInterceptors(InterceptorRegistry registry) {            registry.addInterceptor(customRequestInterceptor).addPathPatterns("/**");    }}